// Using api keu from env var SPARKPOST_API_KEY
const SparkPost = require('sparkpost')
const client = new SparkPost()

// SparkPost template names (it's possible to get this via API)
// TODO move this tp database so it can be changed without downtime
const SIGNUP_CONFIRM_TEMPLATE = 'sign-up-confirmation'
const PASSWORD_RECOVERY_TEMPLATE = 'password-recovery'

const sendSingleEmail = (template, recipient, substitutions) => {
  return client.transmissions.send({
    content: {
      template_id: template,
    },
    recipients: [{
      address: recipient,
      substitution_data: substitutions,
    }],
  })
}

const sendSignupConfirmationEmail = (user, token) => {
  if (process.env.MAILER_ENABLED === '0') {
    return true
  }

  let recipient = user.email
  let name = user.firstName
  // override recipient in dev/test if override is set
  if (process.env.NODE_ENV !== 'production' && process.env.OVERRIDE_EMAIL) {
    recipient = process.env.OVERRIDE_EMAIL
    name = 'NoName'
  }

  return sendSingleEmail(SIGNUP_CONFIRM_TEMPLATE, recipient, {
    name,
    token,
  })
}

const sendPasswordRecoveryEmail = (user, token) => {
  if (process.env.MAILER_ENABLED === '0') {
    return true
  }

  let recipient = user.email
  // override recipient in dev/test if override is set
  if (process.env.NODE_ENV !== 'production' && process.env.OVERRIDE_EMAIL) {
    recipient = process.env.OVERRIDE_EMAIL
  }

  return sendSingleEmail(PASSWORD_RECOVERY_TEMPLATE, recipient, {
    token,
  })
}


module.exports = {
  sendSignupConfirmationEmail,
  sendPasswordRecoveryEmail,
}

const HttpStatus = require('http-status-codes')

class AppException extends Error {
  constructor (message, status = HttpStatus.INTERNAL_SERVER_ERROR) {
    // Calling parent constructor of base Error class.
    super(message)

    // Saving class name in the property of our custom error as a shortcut.
    this.name = this.constructor.name
    this.status = status

    // Capturing stack trace, excluding constructor call from it.
    Error.captureStackTrace(this, this.constructor)
  }
}

class RecordAlreadyExistsException extends AppException {
  constructor (message) {
    super(message || 'Database record already exists', HttpStatus.CONFLICT)
  }
}

class RecordNotFoundException extends AppException {
  constructor (message) {
    super(message || 'Record not found', HttpStatus.NOT_FOUND)
  }
}

class RouteNotFoundException extends AppException {
  constructor (message) {
    super(message || 'Route not found', HttpStatus.NOT_FOUND)
  }
}

class RecordCreationException extends AppException {
  constructor (message) {
    super(message || 'Record could not be created', HttpStatus.INTERNAL_SERVER_ERROR)
  }
}

class AuthenticationException extends AppException {
  constructor (message) {
    super(message || 'Authentication Exception', HttpStatus.UNAUTHORIZED)
  }
}

class InvalidParametersException extends AppException {
  constructor (message) {
    super(message || 'Invalid Parameters Exception', HttpStatus.BAD_REQUEST)
  }
}

class InvalidTokenException extends AppException {
  constructor (message) {
    super(message || 'Invalid Token Exception', HttpStatus.BAD_REQUEST)
  }
}

module.exports = {
  AppException,
  RecordAlreadyExistsException,
  RecordNotFoundException,
  RouteNotFoundException,
  RecordCreationException,
  AuthenticationException,
  InvalidParametersException,
  InvalidTokenException,
}
